mapper.select:
SELECT * FROM truckGoodsReport


        id = #{id}


        truckNumber = #{truckNumber}


        beginDate >= #{beginDate}


        endDate < #{endDate}


        carryNumber = #{carryNumber}


        ','+goodsType+',' like '%,'+#{goodsType}+',%'


        reportStatus = #{reportStatus}


        truckNumber = #{truckNumber}


        driver = #{driver}


        client = #{client}


ORDER BY createDate desc,createTime desc
